INSERT INTO `wp_posts` VALUES (284,1,'2021-04-19 17:57:55','2021-04-19 15:57:55','<!-- wp:paragraph {\"backgroundColor\":\"light-green-cyan\"} -->\n<p class=\"has-light-green-cyan-background-color has-background\"><strong>MOINS </strong>de temps passé à collecter et analyser vos données,<strong>c’es</strong></p>\n<!-- /wp:paragraph -->','Bloc réutilisable sans titre','','publish','closed','closed','','bloc-reutilisable-sans-titre','','','2021-04-19 17:57:55','2021-04-19 15:57:55','',0,'http://0.0.0.0/2021/04/19/bloc-reutilisable-sans-titre/',0,'wp_block','',0);
