INSERT INTO `wp_posts` VALUES (746,1,'2020-06-16 06:56:33','2020-06-16 06:56:33','<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Je fais ma <em>demande !</em></h2>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<form data-netlify=\"true\" id=\"demo-form\">\n    <div class=\"form-row\">\n        <label for=\"demo-form-company\">\n            Société\n            <span class=\"required-label\">*</span>\n        </label>\n        <input type=\"text\" name=\"company\" id=\"demo-form-company\" required=\"\">\n    </div>\n    <div class=\"form-row\">\n        <label for=\"demo-form-email\">\n            E-mail\n            <span class=\"required-label\">*</span>\n        </label>\n        <input type=\"email\" name=\"email\" id=\"demo-form-email\" required=\"\">\n    </div>\n    <div class=\"submit-container\">\n        <button type=\"submit\" class=\"btn btn--green\">\n            Recevoir l\'accès à la démo\n        </button>\n    </div>\n</form>\n\n<script type=\"text/javascript\">\n    const handleDemoFormSubmit = (e) => {\n        e.preventDefault();\n        let demoForm = document.getElementById(\'demo-form\');\n        let formData = new FormData(demoForm);\n        fetch(\'/\', {\n            method: \'POST\',\n            headers: { \"Content-Type\": \"application/x-www-form-urlencoded\" },\n            body: new URLSearchParams(formData).toString()\n        }).then(\n            () => {\n                demoForm.outerHTML =\n                    \"<div>\" +\n                        \"<p><strong>Merci !<strong></p>\" +\n                        \"Cliquez sur ce lien pour accéder à la démo :<br/>\" +\n                        \"👉 <a href=\'https://youtu.be/seJHyHsDhkc\' target=\'_blank\'>Démo</a>\" +\n                    \"</div>\";\n            }\n        ).catch((error) =>\n            alert(\"Une erreur s\'est produite, merci de réessayer dans quelques instants\")\n        );\n    }\n    document.getElementById(\"demo-form\").addEventListener(\"submit\", handleDemoFormSubmit);\n</script>\n<!-- /wp:html -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading {\"textAlign\":\"center\"} -->\n<h2 class=\"has-text-align-center\">Écrivez-nous !</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:html -->\nFaites nous part de votre demande, nous vous répondrons dans les plus brefs délais\n\n<form data-netlify=\"true\" id=\"contact-form\">\n    <div class=\"form-row\">\n        <div class=\"grid2\">\n            <div class=\"mr-1\">\n                <label for=\"contact-form-firstname\">\n                    Nom\n                    <span class=\"required-label\">*</span>\n                </label>\n                <input type=\"text\" name=\"firstname\" id=\"contact-form-firstname\" required=\"\" placeholder=\"Prénom\">\n            </div>\n            <div class=\"ml-1\">\n                <label for=\"contact-form-lastname\" style=\"visibility: hidden;\">\n                    -\n                    <span class=\"required-label\">*</span>\n                </label>\n                <input type=\"text\" name=\"lastname\" id=\"contact-form-lastname\" required=\"\" placeholder=\"Nom\">\n            </div>\n        </div>\n    </div>\n    <div class=\"form-row\">\n        <div class=\"grid2\">\n            <div class=\"mr-1\">\n                <label for=\"contact-form-email\">\n                    E-mail\n                    <span class=\"required-label\">*</span>\n                </label>\n                <input type=\"email\" name=\"email\" id=\"contact-form-email\" required=\"\">\n            </div>\n            <div class=\"ml-1\">\n                <label for=\"contact-form-phone\">\n                    Téléphone\n                    <span class=\"required-label\">*</span>\n                </label>\n                <!-- 0100000000 or +3310000000 will be true -->\n                <input type=\"tel\" pattern=\"(^0[1-9][0-9]{8})|(^\\+[0-9]+)\" name=\"phone\" id=\"contact-form-phone\" required=\"\">\n            </div>\n        </div>\n    </div>\n    <div class=\"form-row\">\n        <label for=\"contact-form-content\">\n            Votre demande\n            <span class=\"required-label\">*</span>\n        </label>\n        <textarea name=\"content\" id=\"contact-form-content\" required=\"\" rows=\"6\"></textarea>\n    </div>\n    <div class=\"submit-container\">\n        <button type=\"submit\" class=\"btn btn--green\">\n            Envoyer ma demande\n        </button>\n    </div>\n</form>\n\n<script type=\"text/javascript\">\n    const handleContactFormSubmit = (e) => {\n        e.preventDefault()\n        let contactForm = document.getElementById(\'contact-form\');\n        let formData = new FormData(contactForm)\n        fetch(\'/\', {\n            method: \'POST\',\n            headers: { \"Content-Type\": \"application/x-www-form-urlencoded\" },\n            body: new URLSearchParams(formData).toString()\n        }).then(\n            () => {\n                contactForm.outerHTML =\n                    \"<div style=\'text-align: center;\'>\" +\n                        \"<p><strong>Merci 👍<strong></p>\" +\n                        \"Nous vous recontacterons très rapidement,<br/>\" +\n                        \"À bientôt !\" +\n                    \"</div>\";\n            }\n        ).catch((error) =>\n            alert(\"Une erreur s\'est produite, merci de réessayer dans quelques instants\")\n        );\n    }\n    document.getElementById(\"contact-form\").addEventListener(\"submit\", handleContactFormSubmit);\n</script>\n<br/><br/>\n<!-- /wp:html -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":1398,\"mediaLink\":\"https://www.toovalu.com/hp_tt_bc/\",\"mediaType\":\"image\",\"imageFill\":true,\"style\":{\"color\":{\"background\":\"#f2c183\"}}} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile is-image-fill has-background\" style=\"background-color:#f2c183\"><figure class=\"wp-block-media-text__media\" style=\"background-image:url(https://www.toovalu.com/wp-content/uploads/2021/06/HP_TT_BC-1024x683.jpg);background-position:50% 50%\"><img src=\"https://www.toovalu.com/wp-content/uploads/2021/06/HP_TT_BC-1024x683.jpg\" alt=\"\" class=\"wp-image-1398 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"placeholder\":\"Contenu…\",\"fontSize\":\"large\"} -->\n<p class=\"has-large-font-size\">Toovalu</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>40, rue Tour d’Auvergne </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> La Halle 6 Est</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>  44200 NANTES</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->','Voir la démo','','publish','closed','closed','','contact','','','2020-06-16 06:56:33','2020-06-16 06:56:33','',0,'https://zakra-agency.sites.qsandbox.com/?page_id=28',0,'page','',0);
